function generateprompt() {
	var rand1;
	rand1 = Math.floor((Math.random() * quotes.length) + 0); 
    document.getElementById("prompt1").innerHTML = quotes[rand1];
}

function shuffle(quotes) {
	let currentIndex = quotes.length, randomIndex;

	// while there are items left to shuffle...
	while (currentIndex != 0) {
	// pick a remaining element...
	randomIndex = Math.floor(Math.random() * currentIndex);

	// decrease
	currentIndex--;

	// swap with current element
	[quotes[currentIndex], quotes[randomIndex]] = [quotes[randomIndex], quotes[currentIndex]];
	}
	return quotes;
}

function displayLinks(num) {
  shuffle(quotes);
  
  for (let i = 0; i < num; i++) {
    var link = "<a href='" + quotes[i][0] + "'>" + quotes[i][1] + "</a><br>";
    document.getElementById('randomPages').innerHTML += link;
   }
}